en.'&agentid='.$id.''; $result = c('curl')->getcurl($url); if(!$this->isempt($result)){ $arr = json_decode($result); $oldrs = $this->getone("`agentid`='$id'"); $this->setbackarr($arr->errcode, $arr->errmsg); if($arr->errcode=='0'){ $uarr = array( 'name' => $arr->name, 'square_logo_url' => $arr->square_logo_url, 'description' => $arr->description, 'allow_userinfos' => json_encode($arr->allow_userinfos), 'allow_partys' => json_encode($arr->allow_partys), 'close' => $arr->close, 'redirect_domain' => $arr->redirect_domain, 'home_url' => $arr->home_url, 'report_location_flag' => $arr->report_location_flag, 'isreportenter' => $arr->isreportenter, ); if($oldrs && isempt($oldrs['square_logo_url'])){ unset($uarr['description']); unset($uarr['redirect_domain']); unset($uarr['home_url']); } $uwhee= ''; if($oldrs && $id=='0'){ $uwhee = "and `name`='".$oldrs['name']."'"; } $this->update($uarr, "`agentid`='$id' ".$uwhee.""); } } return $this->backarr; } public function setagent($id) { $rs = $this->getone("`agentid`='$id'"); if(!$rs)return $this->backarr; if(isempt($rs['square_logo_url'])){ $this->backarr['msg']='请先获取信息在更新'; return $this->backarr; } if(isempt($rs['description']))$rs['description']=$rs['name']; $home_url = str_replace('{agentid}', $id, $rs['home_url']); $menujson = str_replace('{agentid}', $id, $rs['menujson']); $body = '{"agentid": '.$id.',"name":"'.$rs['name'].'","report_location_flag":'.$rs['report_location_flag'].',"description":"'.$rs['description'].'","isreportenter":'.$rs['isreportenter'].',"home_url":"'.$home_url.'"'; if(!isempt($rs['redirect_domain']))$body .= ',"redirect_domain":"'.$rs['redirect_domain'].'"'; $body .= '}'; $body = str_replace('{agentid}', $id, $body); $token = $this->gettoken($id, true); if(isempt($token))return $this->tokenerr(); $url = ''.$this->gettourl('URL_agentset').'?access_token='.$token.''; $result = c('curl')->postcurl($url, $body); if($result!=''){ $arr = json_decode($result); $this->setbackarr($arr->errcode, $arr->errmsg); if($arr->errcode==0){ $this->update(array( 'home_url' => $home_url, 'menujson' => $menujson, ), $rs['id']); } } return $this->backarr; } public function getmenu($id) { $token = $this->gettoken($id, true); if(isempt($token))return $this->tokenerr(); $url = ''.$this->gettourl('URL_menuget').'?access_token='.$token.'&agentid='.$id.''; $result = c('curl')->getcurl($url); if(!$this->isempt($result)){ $arr = json_decode($result, true); $this->setbackarr($arr['errcode'], $arr['errmsg']); $button = $arr['button']; $this->backarr['button'] = $button; } return $this->backarr; } public function menuupdate($id) { $rs = $this->getone("`agentid`='$id'"); if(!$rs)return $this->backarr; $token = $this->gettoken($id, true); if(isempt($token))return $this->tokenerr(); $body = str_replace('{agentid}', $id, $rs['menujson']); $menu = json_decode($body, true); if(!$menu || isempt($body)){ $this->setbackarr(-1, '没有设置菜单,可以选择操作删除菜单'); return $this->backarr; } $body = str_replace('{agentid}', $id, $body); $url = ''.$this->gettourl('URL_menucreate').'?access_token='.$token.'&agentid='.$id.''; $result = c('curl')->postcurl($url, $body); if($result!=''){ $arr = json_decode($result); $this->setbackarr($arr->errcode, $arr->errmsg); } return $this->backarr; } public function menudelete($id) { $rs = $this->getone("`agentid`='$id'"); if(!$rs)return $this->backarr; $token = $this->gettoken($id, true); if(isempt($token))return $this->tokenerr(); $url = ''.$this->gettourl('URL_menudelete').'?access_token='.$token.'&agentid='.$id.''; $result = c('curl')->getcurl($url); if(!$this->isempt($result)){ $arr = json_decode($result, true); $this->setbackarr($arr['errcode'], $arr['errmsg']); } return $this->backarr; } }